import {
  Column,
  CreateDateColumn,
  DeleteDateColumn,
  Entity,
  Index,
  ManyToOne,
  PrimaryGeneratedColumn,
  UpdateDateColumn,
} from 'typeorm';
import { User } from '../users/users.deprecated.entity';
import { CommentableType } from './commentable.enum';

@Entity()
export class Comment {
  @PrimaryGeneratedColumn()
  id: number;

  @Column({
    type: 'enum',
    enum: CommentableType,
  })
  commentableType: CommentableType;

  @Column()
  @Index()
  commentableId: number;

  @Column('text')
  content: string;

  @ManyToOne(() => User)
  createdBy: User;

  @Column()
  @Index()
  createdById: number;

  @CreateDateColumn()
  createdAt: Date;

  @UpdateDateColumn()
  updatedAt: Date;

  @DeleteDateColumn()
  deletedAt?: Date;
}

@Entity()
export class CommentDeleteLog {
  @PrimaryGeneratedColumn()
  id: number;

  @ManyToOne(() => Comment)
  comment: Comment;

  @Column()
  @Index()
  commentId: number;

  @ManyToOne(() => User)
  operatedBy: User;

  @Column()
  @Index()
  operatedById: number;

  @CreateDateColumn()
  createdAt: Date;
}

@Entity()
export class CommentQueryLog {
  @PrimaryGeneratedColumn()
  id: number;

  @ManyToOne(() => Comment)
  comment: Comment;

  @Column()
  @Index()
  commentId: number;

  @ManyToOne(() => User)
  viewer: User;

  @Column('int', { nullable: true })
  @Index()
  viewerId?: number | null;

  @Column()
  ip: string;

  @Column()
  userAgent: string = '';

  @CreateDateColumn()
  createdAt: Date;
}
